package week1.day1;

public class PrintOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i=1;i<=20;i++) {
			if(i%2!=0) {
				System.out.println("The odd number is  "+i);
			}else {
				System.out.println("The even number is " +i);
			}
		}
	}

}
